from shiny import App, render, ui
import numpy as np
from matplotlib import pyplot as plt

app_ui = ui.page_fluid(
    ui.input_slider("n", "N", 0, 20, 1),
    ui.input_checkbox("check","2 or 4"),
    ui.output_text_verbatim("txt"),
    ui.output_plot("plots")
)


def server(input, output, session):
    @output
    @render.text
    def txt():
        y = np.array([1,5,7])
        if input.check():
            x = 2
        else:
            x=4


        return f"n*{x} is {input.n() * x} and {y}"

    @output
    @render.plot
    def plots():
        random_normal = np.random.normal(0, 5, 1000)
        fig = plt.figure(figsize =(5, 3))

        plt.hist(random_normal, bins = input.n())

        plt.title("Numpy Histogram")





app = App(app_ui, server)
